import React, { useEffect, useRef } from 'react';
import '../background.css';
function Background() {
    const canvasRefs = [useRef(null), useRef(null), useRef(null)];

    useEffect(() => {
        const canvas = canvasRefs.map(ref => ref.current),
            background = canvas[0],
            foreground1 = canvas[1],
            foreground2 = canvas[2],
            config = {
                circle: {
                    amount: 18,
                    layer: 3,
                    color: [157, 97, 207],
                    alpha: 0.3
                },
                line: {
                    amount: 12,
                    layer: 3,
                    color: [255, 255, 255],
                    alpha: 0.3
                },
                speed: 0.5,
                angle: 20
            };

        let wWidth, wHeight, timer;

        // Animation functions go here (drawCircle, drawLine, animate, etc.)
        // The code from your original script can be used, modified to use canvas refs directly

        const setCanvasHeight = () => {
            wWidth = window.innerWidth;
            wHeight = window.innerHeight;
            canvas.forEach(c => {
                c.width = wWidth;
                c.height = wHeight;
            });
        };

        const animate = () => {
            // Animate circles and lines with drawing logic, calling requestAnimationFrame(animate)
            timer = requestAnimationFrame(animate);
        };

        const createItem = () => {
            // Set up circles, lines and start animation
            cancelAnimationFrame(timer);
            timer = requestAnimationFrame(animate);
        };

        setCanvasHeight();
        createItem();
        window.addEventListener('resize', setCanvasHeight);

        return () => {
            cancelAnimationFrame(timer);
            window.removeEventListener('resize', setCanvasHeight);
        };
    }, []);

    return (
        <div id="bg">
            <canvas ref={canvasRefs[0]}></canvas>
            <canvas ref={canvasRefs[1]}></canvas>
            <canvas ref={canvasRefs[2]}></canvas>
        </div>
    );
}

export default Background;
